import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import {
  ScheduleComponent,
  ViewsDirective,
  ViewDirective,
  WorkWeek,
  Inject,
  Resize,
  DragAndDrop,
} from '@syncfusion/ej2-react-schedule';

import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';
import { DateTimePickerComponent } from '@syncfusion/ej2-react-calendars';
import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';
import * as dataSource from './datasource.json';
/**
 * Schedule editor template sample
 */
export class EditorTemplate extends SampleBase {
  constructor() {
    super(...arguments);
    this.isTreeItemDropped = false;
    this.draggedItemId = '';
    this.allowDragAndDrops = true;
    this.previousEventTarget = '';
    this.fields = {
      dataSource: dataSource.waitingList,
      id: 'Id',
      text: 'Name',
    };
  }
  scheduleObj;
  allSubjects = [
    { Subject: 'Algebra', Id: '1' },
    { Subject: 'Fizica 1', Id: '2' },
    { Subject: 'Fizica 2', Id: '3' },
  ];
  subjectList = ['Algebra', 'Fizica 1', 'Fizica 2'];
  classes = [
    { name: 'S6N', id: '1' },
    { name: 'S7A', id: '2' },
    { name: 'C4', id: '3' },
    { name: 'C3', id: '4' },
    { name: 'C6', id: '5' },
  ];
  clsList = ['S6N', 'S7A', 'C4', 'C3', 'C6'];
  allowAdd = false;

  onActionBegin(args) {
    debugger;
    if (args.requestType === 'eventCreate') {
      if (!this.allowAdd) {
        debugger;
        var app = {
          Subject: this.subjectList[+args.data[0].Subject - 1],
          SubjectId: args.Subject,
          Class: this.clsList[+args.data[0].Class - 1],
          StartTime: args.data[0].StartTime,
          EndTime: args.data[0].EndTime,
        };
        args.cancel = true;
        this.allowAdd = true;
        this.scheduleObj.addEvent(app);
      } else {
        this.allowAdd = false;
      }
    }
  }

  async onTreeDragStop(event) {
    if (this.previousEventTarget) {
      removeClass([this.previousEventTarget], 'highlight');
    }
    let treeElement = closest(event.target, '.e-treeview');
    let classElement =
      this.scheduleObj.element.querySelector('.e-device-hover');
    if (classElement) {
      classElement.classList.remove('e-device-hover');
    }
    if (!treeElement) {
      event.cancel = true;
      let scheduleElement = closest(event.target, '.e-content-wrap');
      if (scheduleElement) {
        let treeviewData = this.treeObj.fields.dataSource;

        if (event.target.classList.contains('e-work-cells')) {
          const filteredData = treeviewData.filter(
            (item) => item.Id === event.draggedNodeData.id
          );
          let cellData = this.scheduleObj.getCellDetails(event.target);

          var result;
          if (cellData.startTime) {
            this.setMinTimeAndMaxTime(cellData.startTime);
            result = await this.GetClasses(cellData.startTime);
          }

          let eventData = {
            SubjectId: filteredData[0].Id,
            StartTime: cellData.startTime,
            EndTime: cellData.endTime,
            Classes: filteredData[0].Class
          };

          this.scheduleObj.openEditor(eventData, 'Add', true);
          this.isTreeItemDropped = true;
          this.draggedItemId = event.draggedNodeData.id;
        }
      }
    }
  }

  treeTemplate(props) {
    const subjectDetails = props.Subject.split("-");
    return (
        <div id="waiting">
            <div id="waitdetails">
                <div id="waitlist">{subjectDetails[2]}</div>
                <div id="waitsubject">
                    {subjectDetails[1]} - {subjectDetails[0]}
                </div>
            </div>
        </div>
    );
}

  onItemDrag(event) {
    if (event.name === 'drag') {
      if (this.previousEventTarget) {
        removeClass([this.previousEventTarget], 'highlight');
      }
      this.previousEventTarget = event.event.target;
      if (event.event.target.classList.contains('e-work-cells')) {
        addClass([event.event.target], 'highlight');
      }
    }
    if (this.scheduleObj.isAdaptive) {
      let classElement =
        this.scheduleObj.element.querySelector('.e-device-hover');
      if (classElement) {
        classElement.classList.remove('e-device-hover');
      }
      if (event.target.classList.contains('e-work-cells')) {
        addClass([event.target], 'e-device-hover');
      }
    }
    if (document.body.style.cursor === 'not-allowed') {
      document.body.style.cursor = '';
    }
    if (event.name === 'nodeDragging') {
      let dragElementIcon = document.querySelectorAll(
        '.e-drag-item.treeview-external-drag .e-icon-expandable'
      );
      for (let i = 0; i < dragElementIcon.length; i++) {
        dragElementIcon[i].style.display = 'none';
      }
      if (this.previousEventTarget) {
        removeClass([this.previousEventTarget], 'highlight');
      }
      if (event.target.classList.contains('e-work-cells')) {
        this.previousEventTarget = event.target;
        addClass([event.target], 'highlight');
      }
    }
  }

  dragStop(event) {
    if (this.previousEventTarget) {
      removeClass([this.previousEventTarget], 'highlight');
    }
  }

  onEventRendered(args) {
    debugger;
    if (args.data.SubjectType === "Course") {
      args.element.style.backgroundColor = "#357cd2";
    } else if (args.data.SubjectType === "Lab") {
      args.element.style.backgroundColor = "#ea7a57";
    } else if (args.data.SubjectType === "Seminar") {
      args.element.style.backgroundColor = "#D1C61B";
    }
  }

  eventTemplate(props) {
    return (<div className="template-wrap">
      <div className="Subject" >{props.Subject}</div>
      <div className="class">{props.Class}</div>
      <div className="semiGroup">{props.SemiGroup}</div>
      <div className="teacher">{props.Teacher}</div></div>)
  }

  async onActionBegin(event) {
    const sched =
    {
      Class: "S6N",
      EndTime: event.data[0].EndTime,
      StartTime: event.data[0].StartTime,
      Id: event.data[0].Id,
      Subject: "Algebra matematica",
      SubjectType: "Course",
      Teacher: "Cristian Vladimirescu"
    };
    debugger;
    if (event.requestType === 'eventCreate') {
      if (!this.allowAdd) {
        let startTimeTest = event.data[0].StartTime
        let endTimeTest = event.data[0].EndTime
        event.cancel = true;
        try {
          const response = await this.props.createSchedule({
            startTime: format(new Date(startTimeTest), 'yyyy-MM-dd kk:mm:ss'),
            endTime: format(new Date(endTimeTest), 'yyyy-MM-dd kk:mm:ss'),
            classId: event.data[0].ClassId,
            subjectId: event.data[0].SubjectId,
            semigroupId: this.props.semiGroupId
          })
          if (response.success) {
            response.scheduling.startTime = startTimeTest;
            response.scheduling.endTime = endTimeTest;
            this.allowAdd = true;
            this.scheduleObj.addEvent(response.scheduling);
          }
        } catch (error) {
          console.log(error);
        }
        // }).then((result) => {
        //     debugger;
        //     result.scheduling.startTime = startTimeTest;
        //     result.scheduling.endTime = endTimeTest;
        //     this.allowAdd = true;
        //     this.scheduleObj.addEvent(result.scheduling);
        // })
      }
      else {
        this.allowAdd = false;
      }
    }
    if (event.requestType === 'eventCreate' && this.isTreeItemDropped) {
      let treeViewData = this.treeObj.fields.dataSource;
      const filteredSubjects = treeViewData.filter(
        (item) => item.Id !== this.draggedItemId
      );
      this.treeObj.fields.dataSource = filteredSubjects;
      let elements = document.querySelectorAll(
        '.e-drag-item.treeview-external-drag'
      );
      for (let i = 0; i < elements.length; i++) {
        remove(elements[i]);
      }
    }
  }


  editorTemplate(props) {
    return props !== undefined ? (
      <table
        className="custom-event-editor"
        style={{ width: '100%' }}
        cellPadding={5}
      >
        <tbody>
          <tr>
            <td className="e-textlabel">Subject</td>
            <td colSpan={4}>
              <DropDownListComponent
                id="subject"
                placeholder="Subject - Teacher"
                data-name="Subject"
                className="e-field"
                style={{ width: '100%' }}
                dataSource={this.allSubjects}
                fields={{ text: 'Subject', value: 'Id' }}
                value={props.SubjectId || null}
              ></DropDownListComponent>
            </td>
          </tr>
          <tr>
            <td className="e-textlabel">Clasa</td>
            <td colSpan={4}>
              <DropDownListComponent
                id="class"
                placeholder="Clasa"
                data-name="Class"
                className="e-field"
                style={{ width: '100%' }}
                dataSource={this.classes}
                fields={{ text: 'name', value: 'id' }}
              ></DropDownListComponent>
            </td>
          </tr>
          <tr>
            <td className="e-textlabel">De la</td>
            <td colSpan={4}>
              <DateTimePickerComponent
                format="HH:mm"
                timeFormat="HH:mm"
                strictMode={true}
                step={120}
                id="StartTime"
                name="StartTime"
                value={new Date(props.StartTime)}
                className="e-field"
              ></DateTimePickerComponent>
            </td>
          </tr>
          <tr>
            <td className="e-textlabel">Pana la</td>
            <td colSpan={4}>
              <DateTimePickerComponent
                format="HH:mm"
                timeFormat="HH:mm"
                strictMode={true}
                step={120}
                id="EndTime"
                name="EndTime"
                value={props.EndTime}
                className="e-field"
              ></DateTimePickerComponent>
            </td>
          </tr>
        </tbody>
      </table>
    ) : (
      <div></div>
    );
  }
  render() {
    return (
      <div className="schedule-control-section">
        <div className="col-lg-12 control-section">
          <div className="control-wrapper">
            <ScheduleComponent
              currentView='WorkWeek'
              width="100%"
              height="650px"
              selectedDate={new Date(2021, 1, 15)}
              ref={(schedule) => (this.scheduleObj = schedule)}
              eventSettings={{ dataSource: [] }}
              editorTemplate={this.editorTemplate.bind(this)}
              eventRendered={this.onEventRendered.bind(this)}
              showHeaderBar={false}
              showQuickInfo={false}
              actionBegin={this.onActionBegin.bind(this)}
              drag={this.onItemDrag.bind(this)}
              dragStop={this.dragStop.bind(this)}
            >
              <ViewsDirective>
                <ViewDirective option='WorkWeek' startHour='08:00' endHour='20:00' />
              </ViewsDirective>
              <Inject
                services={[WorkWeek, Resize, DragAndDrop]}
              />
            </ScheduleComponent>
          </div>
          <div className="treeview-container">
            <div className="title-container">
              <h1 className="title-text"> Materii </h1>
            </div>
            <TreeViewComponent
              ref={(tree) => (this.treeObj = tree)}
              cssClass="treeview-external-drag"
              dragArea=".drag-sample-wrapper"
              nodeTemplate={this.treeTemplate.bind(this)}
              fields={this.fields}
              nodeDragStop={this.onTreeDragStop.bind(this)}
              nodeDragging={this.onItemDrag.bind(this)}
              allowDragAndDrop={this.allowDragAndDrops}
            />
          </div>
        </div>
      </div>
    );
  }
}

render(<EditorTemplate />, document.getElementById('sample'));
